let burger=document.querySelector(".header__menu-btn"),close=document.querySelector(".close-btn"),menu=document.querySelector(".header__menu");burger.addEventListener("click",(function(){menu.style.display="block",menu.style.animation="menu-down 0.5s forwards"}));let hideMenu=function(){menu.style.display="none"};close.addEventListener("click",(function(){menu.style.animation="menu-up 0.5s forwards",setTimeout(hideMenu,500)}));